function setOption(name,value){
	const { java, source, cookie } = this
	v=String(source.getVariable())
	if (value!=undefined&&v!=""){
		v=v.split(/,(.+)?/,2)
		let options= v[1]||"{}"
		options=JSON.parse(options)
		options[name]=value
		v[1]=JSON.stringify(options)
		source.setVariable(v.join(","))
		java.toast("\n成功修改当前网址为\n"+source.getVariable())
		}
	}

